					<Text
						style={{
							color: Colors.black,
							fontSize: 18,
							fontWeight: "bold",
						}}
					>
						Login
					</Text>
				</TouchableOpacity>
			</View>
		<SafeAreaView>
		<View><ModalTester isVisible={isVisible} /></View>
		</SafeAreaView>
		</SafeAreaView>
	);
};

